
(function(){tinymce.PluginManager.requireLangPack('imagechooser');tinymce.create('tinymce.plugins.ImageChooserPlugin',{init:function(ed,url){ed.addCommand('mceImageChooser',function(){var dialog=window.open('/_adminui/image_chooser','status: no; location: no;','width=480, height=600');$(dialog.document).ready(function(){window.appendImage=dialog.appendImage=function(image_path){var img='<img width="500" src="'+image_path+'"/>';ed.execCommand('mceInsertContent',false,img);setTimeout(function(){dialog.close();},1000);};});});ed.addButton('imagechooser',{title:'imagechooser.desc',cmd:'mceImageChooser',image:url+'/img/icon_photo_upload_16px.gif'});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'ImageChooser plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/imagechooser',version:"1.0"};}});tinymce.PluginManager.add('imagechooser',tinymce.plugins.ImageChooserPlugin);})();